/**
 * Inline development version. Only to be used while developing since it uses document.write to load scripts.
 */

/*jshint smarttabs:true, undef:true, latedef:true, curly:true, bitwise:true, camelcase:true */
/*globals $code */

(function(exports) {
    "use strict";

    var html = "",
        baseDir = "",
        s = "";

    var scripts = document.getElementsByTagName('script');
    for (var i = 0; i < scripts.length; i++) {
        var src = scripts[i].src;

        if (src.indexOf('/plugin.min.js') != -1) {
            baseDir = src.substring(0, src.lastIndexOf('/'));
        }
    }

    function writeScripts() {
        document.write(html);
    }

    function load(path) {
        html += '<script type="text/javascript" src="' + baseDir + '/' + path + '"></script>\n';
    }

    load('lib/support.js');
    load('lib/tips.js');
    load('lib/ui.js');
    load('lib/datalist.js');
    load('lib/colorpicker.js');
    load('lib/tinymce.popup.js')
    load('lib/plugin.js');

    load('lib/punycode.js');
    load('lib/url.js');
    load('lib/string.js');
    load('lib/json.js');
    load('lib/modal.js');

    load('lib/dialog.js');
    load('lib/tree.js');

    load('lib/extensions.js');
    load('lib/aggregator.js');
    load('lib/popups.js');

    writeScripts();
})(this);