        if (node.attr('class')) {
          cls = node.attr('class').replace(/\s*upload-placeholder\s*/, '').split(' ');
        }

        // add marker classes
        cls.push('mce-item-upload');
        cls.push('mce-item-upload-marker');

        if (node.name === 'media') {
          node.name = 'img';
          node.shortEnded = true;
        }

